// Map circuit names from Ergast API to our track keys
export function getCircuitKey(circuitName = "") {
  const normalized = circuitName
    .toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, ""); // remove accents

  if (normalized.includes("bahrain")) return "bahrain";
  if (normalized.includes("jeddah")) return "jeddah";
  if (normalized.includes("albert park")) return "albert_park";
  if (normalized.includes("suzuka")) return "suzuka";
  if (normalized.includes("shanghai")) return "shanghai";
  if (normalized.includes("miami")) return "miami";
  if (normalized.includes("imola")) return "imola";
  if (normalized.includes("monaco")) return "monaco";
  if (normalized.includes("villeneuve")) return "villeneuve";
  if (normalized.includes("barcelona")) return "catalunya";
  if (normalized.includes("red bull ring")) return "red_bull_ring";
  if (normalized.includes("silverstone")) return "silverstone";
  if (normalized.includes("hungaroring")) return "hungaroring";
  if (normalized.includes("spa")) return "spa";
  if (normalized.includes("zandvoort")) return "zandvoort";
  if (normalized.includes("monza")) return "monza";
  if (normalized.includes("baku")) return "baku";
  if (normalized.includes("marina bay")) return "marina_bay";
  if (normalized.includes("americas")) return "cota";
  if (normalized.includes("rodriguez")) return "rodriguez";
  if (normalized.includes("jose carlos pace")) return "interlagos";
  if (normalized.includes("las vegas")) return "las_vegas";
  if (normalized.includes("losail")) return "losail";
  if (normalized.includes("yas marina")) return "yas_marina";

  return "generic";
}

